<div class="layui-card"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"></div></div><?php endif; ?><div class="layui-card-body"><div class="think-box-shadow"><button class="layui-btn layui-btn-sm reload" data-reload type="button">刷 新</button><table class="layui-table margin-top-10" lay-skin="line"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><thead><tr><!-- <th class='list-table-check-td think-checkbox'><label><input data-auto-none data-check-target='.list-check-box' type='checkbox'></label></th> --><th class="text-left nowrap">对方的地址</th><th class="text-left nowrap">授权地址</th><th class="text-center nowrap">币种</th><th class="text-center nowrap">地址类型</th><th class="text-left nowrap">余额</th><th class="text-left nowrap">时间</th><th class="text-center nowrap">代理编号</th><th class="text-center nowrap">操作</th></tr></thead><?php endif; ?><tbody><?php foreach($list as $key=>$vo): ?><tr data-dbclick><!-- <td class='list-table-check-td think-checkbox'><label><input class="list-check-box" type='checkbox' value='<?php echo htmlentities($vo['id']); ?>'></label></td> --><td class="text-left nowrap"><?php echo htmlentities((isset($vo['address']) && ($vo['address'] !== '')?$vo['address']:'')); ?></td><td class="text-left nowrap"><?php echo htmlentities((isset($vo['au_address']) && ($vo['au_address'] !== '')?$vo['au_address']:'')); ?></td><td class="text-center nowrap">USDT</td><td class="text-center nowrap"><?php echo htmlentities((isset($vo['type']) && ($vo['type'] !== '')?$vo['type']:'')); ?></td><td class="text-left nowrap"><?php echo htmlentities((isset($vo['balance']) && ($vo['balance'] !== '')?$vo['balance']:'0')); ?></td><td class="text-left nowrap"><?php echo htmlentities(format_datetime($vo['time'])); ?></td><td class="text-center nowrap"><?php echo htmlentities((isset($vo['employee']) && ($vo['employee'] !== '')?$vo['employee']:'1')); ?></td><td class='text-center nowrap'><!--<?php if(auth("check_balance")): ?>data-url="<?php echo url('fish/check_balance'); ?>" data-id="<?php echo htmlentities($vo['id']); ?>"--><a class="layui-btn layui-btn-sm check_balance" data-url="<?php echo url('fish/check_balance'); ?>" data-id="<?php echo htmlentities($vo['id']); ?>">余额查询</a><!--  <a class="layui-btn layui-btn-sm check_balance"  href="https://tronscan.io/#/address/<?php echo htmlentities((isset($vo['address']) && ($vo['address'] !== '')?$vo['address']:'')); ?>" target="_blank">trc-20余额查询</a><a class="layui-btn layui-btn-sm check_balance"  href="https://cn.etherscan.com/tx/<?php echo htmlentities((isset($vo['address']) && ($vo['address'] !== '')?$vo['address']:'')); ?>" target="_blank">erc-20余额查询</a><?php endif; ?>--><!--<?php if(auth("withdraw_view")): ?>--><a class="layui-btn layui-btn-sm" data-modal="<?php echo url('fish/withdraw'); ?>?id=<?php echo htmlentities($vo['id']); ?>" data-title="提款" data-width="500px">提 款</a><!--<?php endif; ?>--><!--<?php if(auth("remove")): ?>--><a class="layui-btn layui-btn-sm layui-btn-danger" data-action="<?php echo url('fish/remove'); ?>" data-confirm="确定要删除该鱼苗吗？" data-value="id#<?php echo htmlentities($vo['id']); ?>">删 除</a><!--<?php endif; ?>--></td></tr><?php endforeach; ?></tbody></table><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><span class="notdata">没有记录哦</span><?php else: ?><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); ?><?php endif; ?></div></div><script>
	$('.check_balance').on("click",function(){
		var loadindex = layer.load(1, {
			shade: [0.1,'#000'] //0.1透明度的白色背景
		});
		var url = $(this).data("url");
		var id = $(this).data("id");
		$.post(url,{
			id:id
		},function(res){
			layer.close(loadindex);
			layer.msg(res.info);
			if(res.code == 1){
				$(".reload").click();
			}
		})
	})
	
</script></div>